class Validate {

	static isNumber(value) {
		return !isNaN(parseFloat(value)) && isFinite(value);
	}

	static checkQuantity(value)  {
		if(value >=1 &&  value < 5 || Validate.isNumber(value) === true ) {
			return true;
		}

		return false;
	}
}

export default Validate;
